    int from, to;
    int arrival;
    int length;
    int end;
    int processed = 100;
    int completed = 50;
    int blocke = 10;
    int busy = 40;
    int clock = 0;
    int max = 3, inuse = 0, stat;
    int used[][] = new int[3][3];
    int delay[] = new int[3]; // Keep for UI compatibility
    
    // Enhanced queue management for proper Erlang-C simulation
    ArrayList<QueuedCall> waitingQueue = new ArrayList<>();
    PriorityQueue<Event> eventQueue = new PriorityQueue<>(new Comparator<Event>() {
        public int compare(Event e1, Event e2) {
            return Integer.compare(e1.time, e2.time);
        }
    });
    
    // Statistics for Erlang-C model
    double totalWaitTime = 0;
    int callsDelayed = 0;
    int totalCalls = 0;
    int callsAnswered = 0;
    
    // Inner classes for better simulation structure
    class QueuedCall {
        int from, to, length, arrivalTime;
        
        QueuedCall(int from, int to, int length, int arrivalTime) {
            this.from = from;
            this.to = to;
            this.length = length;
            this.arrivalTime = arrivalTime;
        }
    }
    
    class Event {
        int time;
        String type; // "ARRIVAL", "COMPLETION"
        int from, to, linkIndex;
        
        Event(int time, String type) {
            this.time = time;
            this.type = type;
        }
        
        Event(int time, String type, int from, int to, int linkIndex) {
            this.time = time;
            this.type = type;
            this.from = from;
            this.to = to;
            this.linkIndex = linkIndex;
        }
    }

    public void inite() {
        String fromt, tot, lengtht, arrivalt;
        try {
            fromt = jTextField9.getText().trim();
            tot = jTextField10.getText().trim();
            lengtht = jTextField12.getText().trim();
            arrivalt = jTextField14.getText().trim();
            
            // Validate that all fields are not empty
            if (fromt.isEmpty() || tot.isEmpty() || lengtht.isEmpty() || arrivalt.isEmpty()) {
                javax.swing.JOptionPane.showMessageDialog(this, 
                    "Please fill in all fields (From Line, To Line, Call Length, Arrival Time)", 
                    "Input Error", javax.swing.JOptionPane.ERROR_MESSAGE);
                return;
            }
            
            // Parse the values
            from = Integer.parseInt(fromt);
            to = Integer.parseInt(tot);
            length = Integer.parseInt(lengtht);
            arrival = Integer.parseInt(arrivalt);
            
            // Validate ranges
            if (from < 1 || from > 8 || to < 1 || to > 8) {
                javax.swing.JOptionPane.showMessageDialog(this, 
                    "From Line and To Line must be between 1 and 8", 
                    "Input Error", javax.swing.JOptionPane.ERROR_MESSAGE);
                return;
            }
            
            if (length <= 0 || arrival < 0) {
                javax.swing.JOptionPane.showMessageDialog(this, 
                    "Call Length must be positive and Arrival Time must be non-negative", 
                    "Input Error", javax.swing.JOptionPane.ERROR_MESSAGE);
                return;
            }
            
        } catch (NumberFormatException e) {
            javax.swing.JOptionPane.showMessageDialog(this, 
                "Please enter valid integers in all fields", 
                "Input Error", javax.swing.JOptionPane.ERROR_MESSAGE);
            return;
        }
        
        // Process the new call with improved logic
        processNewCall();
        
        // Update display
        putData();
        repaint();
    }
    
    // New method for improved call processing
    public void processNewCall() {
        totalCalls++;
        
        // Set clock to current arrival time or later if processing events
        if (arrival > clock) {
            clock = arrival;
        }
        
        // Process any completed calls up to current time
        processEventQueue();
        
        // Check call status
        stat = callstat();
        jTextField28.setText(String.valueOf(clock));
        
        if (stat == 0) {
            // Call can be connected immediately
            end = arrival + length;
            connection();
            used[inuse][0] = from;
            used[inuse][1] = to;
            used[inuse][2] = end;
            inuse++;
            completed++;
            processed++;
            callsAnswered++;
            
            // Schedule completion event
            eventQueue.offer(new Event(end, "COMPLETION", from, to, inuse - 1));
            
        } else if (stat == 1) {
            // Lines busy - add to queue (Erlang-C behavior)
            QueuedCall queuedCall = new QueuedCall(from, to, length, arrival);
            waitingQueue.add(queuedCall);
            callsDelayed++;
            
            // Update delay display for UI compatibility
            if (waitingQueue.size() > 0) {
                QueuedCall firstInQueue = waitingQueue.get(0);
                delay[0] = firstInQueue.from;
                delay[1] = firstInQueue.to;
                delay[2] = firstInQueue.length;
            }
            
        } else if (stat == 2) {
            // All links busy AND lines busy - call blocked
            blocke++;
            processed++;
        }
    }
    
    // Process events in chronological order
    public void processEventQueue() {
        while (!eventQueue.isEmpty() && eventQueue.peek().time <= clock) {
            Event event = eventQueue.poll();
            
            if (event.type.equals("COMPLETION")) {
                // A call has completed - free the lines
                int fromLine = event.from - 1;
                int toLine = event.to - 1;
                line[fromLine] = 0;
                line[toLine] = 0;
                
                // Remove from used array
                for (int i = 0; i < inuse; i++) {
                    if (used[i][0] == event.from && used[i][1] == event.to) {
                        // Shift remaining calls down
                        for (int j = i; j < inuse - 1; j++) {
                            used[j][0] = used[j + 1][0];
                            used[j][1] = used[j + 1][1];
                            used[j][2] = used[j + 1][2];
                        }
                        used[inuse - 1][0] = used[inuse - 1][1] = used[inuse - 1][2] = 0;
                        inuse--;
                        break;
                    }
                }
                
                // Try to serve next call in queue
                processWaitingQueue();
            }
        }
    }
    
    // Process waiting queue when resources become available
    public void processWaitingQueue() {
        if (waitingQueue.isEmpty()) {
            // Clear delay display
            delay[0] = delay[1] = delay[2] = 0;
            return;
        }
        
        // Check if we can serve the first call in queue
        QueuedCall nextCall = waitingQueue.get(0);
        
        // Check if required lines are now free and we have available links
        if (line[nextCall.from - 1] == 0 && line[nextCall.to - 1] == 0 && inuse < max) {
            // Serve the call
            waitingQueue.remove(0);
            
            // Calculate wait time
            int waitTime = clock - nextCall.arrivalTime;
            totalWaitTime += waitTime;
            
            // Set up the connection
            line[nextCall.from - 1] = line[nextCall.to - 1] = 1;
            used[inuse][0] = nextCall.from;
            used[inuse][1] = nextCall.to;
            used[inuse][2] = clock + nextCall.length;
            inuse++;
            
            completed++;
            processed++;
            callsAnswered++;
            
            // Schedule completion
            eventQueue.offer(new Event(clock + nextCall.length, "COMPLETION", 
                                     nextCall.from, nextCall.to, inuse - 1));
            
            // Update delay display with next call in queue (if any)
            if (!waitingQueue.isEmpty()) {
                QueuedCall firstInQueue = waitingQueue.get(0);
                delay[0] = firstInQueue.from;
                delay[1] = firstInQueue.to;
                delay[2] = firstInQueue.length;
            } else {
                delay[0] = delay[1] = delay[2] = 0;
            }
            
            // Recursively check if we can serve more calls
            processWaitingQueue();
        }
    }

