    // Method to get enhanced statistics
    public String getStatistics() {
        double avgWaitTime = callsDelayed > 0 ? totalWaitTime / callsDelayed : 0;
        double answerRate = totalCalls > 0 ? (double) callsAnswered / totalCalls * 100 : 0;
        double blockingRate = totalCalls > 0 ? (double) blocke / totalCalls * 100 : 0;
        
        return String.format("Enhanced Erlang-C Statistics:\n" +
                           "Total Calls: %d\n" +
                           "Calls Answered: %d\n" +
                           "Calls in Queue: %d\n" +
                           "Calls Delayed: %d\n" +
                           "Blocked Calls: %d\n" +
                           "Average Wait Time: %.2f units\n" +
                           "Answer Rate: %.1f%%\n" +
                           "Blocking Rate: %.1f%%",
                           totalCalls, callsAnswered, waitingQueue.size(), 
                           callsDelayed, blocke, avgWaitTime, answerRate, blockingRate);
    }

