    public void callcompletecheck() {
        int i;
        for (i = 0; i < inuse; i++) {
            if (used[i][2] <= arrival && used[i][2] > 0) {
                completed++;
                processed++;
                int fromLine = used[i][0] - 1;
                int toLine = used[i][1] - 1;
                line[fromLine] = 0;
                line[toLine] = 0;
                clock = used[i][2];
                for (int j = i; j < inuse - 1; j++) {
                    used[j][0] = used[j + 1][0];
                    used[j][1] = used[j + 1][1];
                    used[j][2] = used[j + 1][2];
                }
                used[inuse - 1][0] = used[inuse - 1][1] = used[inuse - 1][2] = 0;
                inuse--;
                delaycheck();
                i--;

            } else
                clock = arrival;
        }
    }

    public int callstat() {
        stat = 0;
        
        // Check if either line is busy (caller or receiver)
        if (line[from - 1] == 1 || line[to - 1] == 1) {
            // Lines are busy - in Erlang-C, this goes to queue
            busy++;
            stat = 1;
            return stat;
        }
        
        // Lines are free, check if we have available switching capacity
        if (inuse >= max) {
            // All switching links are busy - call blocked
            blocke++;
            processed++;
            stat = 2;
            return stat;
        }
        
        // Both lines free and switching capacity available
        return 0; // Call can be connected immediately
    }

